    JOIN 
        tutores t ON v.tutores_id = t.id
    JOIN 
        institucion inst ON v.institucion_id = inst.id
    JOIN 
        carrera c ON e.carrera_id = c.id
    ORDER BY 
        v.fecha_inicio DESC;
